sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {
	},
	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var today = new GlideDateTime(); today = today.getDate(); // set time to midnight
		var testdate = new GlideDateTime(strDate); testdate = testdate.getDate(); // set time to midnight

		// If the testdate is before rightnow, return true, else return false
		return testdate.before(today);
	},
	isDateToday: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var today = new GlideDateTime(); today = today.getDate(); // set time to midnight
		var testdate = new GlideDateTime(strDate); testdate = testdate.getDate(); // set time to midnight

		// If the testdate is today, return true, else return false
		return testdate.getDayOfMonthLocalTime() == today.getDayOfMonthLocalTime();
	},
	type: 'NeedItUtils'
};
